package com.example.demo.config;import com.example.demo.conditional.LinuxCondition;import com.example.demo.conditional.LinuxListService;import com.example.demo.conditional.ListService;import com.example.demo.conditional.WindowsCondition;import com.example.demo.conditional.WindowsListService;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Conditional;import org.springframework.context.annotation.Configuration;@Configurationpublic class ConditionConfig {    @Bean    @Conditional(WindowsCondition.class)    public ListService windowsListService() {        return new WindowsListService();    }    @Bean    @Conditional(LinuxCondition.class)    public ListService linuxListService() {        return new LinuxListService();    }}