package com.example.demo.config;import com.example.demo.pojo.Person;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;/** * @ Configuration申明当前类是一个配置类，相当于配置一个xml *  * @ ComponentScan自动扫描包名下所有使用的@Service,@Component,@Repository,@Controller的类并且注册成Bean */@Configuration@ComponentScan(value = { "com.example.demo.service", "com.example.demo.aop", "com.example.demo.component",        "com.example.demo.dao", "com.example.demo.pojo" })public class DemoConfig {    @Bean(initMethod = "init", destroyMethod = "destroy")    public Person getPerson() {        return new Person();    }}