package com.example.demo.config;import com.example.demo.directive.IndexOfMethod;import freemarker.template.Configuration;import freemarker.template.TemplateModelException;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import javax.annotation.PostConstruct;@Componentpublic class FreemarkerConfig {    @Autowired    private Configuration configuration;    @Autowired    private IndexOfMethod indexOfMethod;    @PostConstruct    public void setSharedVariable() throws TemplateModelException {        configuration.setSharedVariable("indexOf", indexOfMethod);    }}