package com.example.demo.config;import com.example.demo.directive.IndexOfMethod;import org.springframework.beans.BeansException;import org.springframework.context.ApplicationContext;import org.springframework.context.ApplicationContextAware;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.EnableWebMvc;@Configuration@EnableWebMvc@ComponentScan(value = { "com.example.demo.mvc" })public class MyMvcConfig implements ApplicationContextAware {    private ApplicationContext context;    @Bean    public IndexOfMethod indexOfMethod() {        return new IndexOfMethod();    }    @Override    public void setApplicationContext(ApplicationContext applicationContext) throws BeansException {        this.context = applicationContext;    }}