package com.example.demo.config;import com.example.demo.pojo.DemoBean;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.Profile;import org.springframework.stereotype.Component;@Component@Configurationpublic class ProfileConfig {    @Bean    @Profile("dev")    public DemoBean devDemoBean() {        return new DemoBean("from dev");    }    @Bean    @Profile("prod")    public DemoBean prodDemoBean() {        return new DemoBean(" from prod");    }}