package com.example.demo.controller;import com.example.demo.service.FunctionService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class HelloController {    @Autowired    private FunctionService service;    @Value("${books.author}")    private String bookName;    @RequestMapping(value = "demo")    public String demo() {        System.out.println(bookName);        return service.sayHello("Word");    }}