package com.example.demo.pojo;public class DemoBean {    private String content;    public DemoBean(String content) {        System.out.println("DemoBean对象创建中" + content);        this.content = content;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }    @Override    public String toString() {        return "DemoBean{" + "content='" + content + '\'' + '}';    }}