package com.example.demo.schedule;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Service;import java.text.SimpleDateFormat;import java.util.Date;//@Servicepublic class ScheduledExecutorService {    private static final SimpleDateFormat sdf = new SimpleDateFormat("HH:mm:ss");    @Scheduled(fixedRate = 300000)    public void reportCurrentTime() {        System.out.println("每五秒执行一次" + sdf.format(new Date()));    }    @Scheduled(cron = "0 0 15 ? * *")    public void fixtimeExecution() {        System.out.println("指定时间" + sdf.format(new Date()) + "执行");    }}