package com.example.demo.service;import org.springframework.beans.factory.BeanNameAware;import org.springframework.context.ResourceLoaderAware;import org.springframework.core.io.Resource;import org.springframework.core.io.ResourceLoader;import org.springframework.stereotype.Service;@Servicepublic class AwareService implements BeanNameAware, ResourceLoaderAware {    private String beanName;    private ResourceLoader loader;    @Override    public void setBeanName(String beanName) {        this.beanName = beanName;    }    @Override    public void setResourceLoader(ResourceLoader resourceLoader) {        this.loader = resourceLoader;    }    public void outputResult() {        System.out.println("bean的名称为:" + beanName);        Resource resouce = loader.getResource("classpath:application.properties");        assert resouce != null;    }}