package com.example.demo.service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * @ Autowired将FunctionService的实体bean注入到UseFunctionService中，形成一种组合关系 */@Servicepublic class UseFunctionService {    @Autowired    private FunctionService functionService;    public String sayHello(String word) {        awareService.outputResult();        return functionService.sayHello(word);    }    private AwareService awareService;    @Autowired    public void setAwareService(AwareService awareService) {        this.awareService = awareService;    }}